var MODULE_NAME = 'core.js';
var Core = {
    response: new Object(),
    doRequest: function(control, action, data, socket, io) {
        var controller = require(MODULE_NAME + "/controller/" + control + "Controller.js");
        controller[action](data, socket, io);
    },
    getResponse: function() {
        return Core.response;
    },
    setResponse: function(code, data) {
        Core.response = {
            code: code,
            data: data,
            message: ''
        };
    }
};
module.exports = Core;