var userModel = {
    verify: function(data, _response){
        var _db = require('core.js/db/DB.js');
        var db = _db.connect();        
        sql = 'SELECT * FROM `users` WHERE `username` = ' + db.escape(data.username) + ' AND `password` = ' + db.escape(data.password);
        db.query(sql, function(err, rows) {
            _response(null,rows);
        });
    }
};
exports.userModel = userModel;
exports.verify = function(data, _response){
    return userModel.verify(data, _response);
};